jQuery(document).ready(function($){var clickAllowed=true;var windowWidth=$(window).width();function onResize(){if($(window).width()!=windowWidth){windowWidth=$(window).width();if($(window).width()<841){clickAllowed=true;$('.main-navigation, .social-links').hide();}else{clickAllowed=false;$('.main-navigation, .social-links').show();}}};$(window).on('resize load',onResize);$('ul.toggle-bar a').click(function(){var tab_id=$(this).attr('data-tab');$('ul.toggle-bar li a').removeClass('current');$('.tab-content').removeClass('current');$(this).addClass('current');$("#"+tab_id).addClass('current');$('a[role="tab"]:not(current_tab)').attr('aria-selected','false');$(this).attr('aria-selected','true');$('div[role="tabpanel"]:not( .current )').attr('aria-hidden','true');$('div.current[role="tabpanel"]').attr('aria-hidden','false');return false;});$('ul.toggle-bar a:not(.nav-toggle)').click(function(){if(clickAllowed===true){$('.main-navigation, .social-links').hide();return false;}});$('.current .nav-toggle').click(function(){if(clickAllowed===true){$('.main-navigation, .social-links').fadeToggle(150);return false;}});$('.toggle-bar a').click(function(){$('.fa-folder-open').hide();$('.fa-folder').show();return false;});$('.folder-toggle').click(function(){$('.fa-folder,.fa-folder-open').toggle();$('#secondary').resize();return false;});$(document).on("webkitfullscreenchange mozfullscreenchange fullscreenchange",function(){$(".site-header").toggleClass('fullscreen');});});